export const StatusContext = createContext()

const StatusContextProvider = (props) => {

    const [statusChange, setStatusChange] = useState(0)

    return (
        <StatusContext.Provider value={{statusChange, setStatusChange }}>
            {props.children}
        </StatusContext.Provider>
    )
}

