import React, { useEffect, useState } from 'react'
import "./cart.css"
export default function Cart({cart, removeFromCart}) {

    const [totalPrice, setTotalPrice]= useState(0)


    useEffect(() => {

        cart.map((item) => (
            setTotalPrice(totalPrice + item.price)
        ))

    }, [cart])
  
  return (
      
    <div className="cart">
        <h1>Cart</h1>
        {cart.map((item) => {
            return <div className='cart-item' key={item._id}>
                   
                <span>{item.name}</span>
                <span>${item.price}</span>
                <button onClick={() => removeFromCart(item._id)}>X</button>
                
            </div>
        })}
        <span className='total-price'>Total Price : ${totalPrice}</span>
        {totalPrice === 0 ? (<span>Add Something to cart !</span>) : <button>Order now</button>}
    </div>
  )
}
