import React from 'react'
import "./order.css"

export default function Order({order}) {
    if(!order){
        return <div></div>
    }else {

        return (
            <div>
       <p>{order.address}</p>
       <p>{order.customer}</p>
       <p>{order.orderItems.map((food)=>{
           return <div>
               <p>{food.name} <span> ${food.price}</span></p>
                
           </div>
       })}</p>
       <p>$ {order.total}</p>
    </div>
  )
}
}
