import React , {useState}from 'react'
import "./addProductModal.css"
export default function AddProductModal({categories, show, onClose}) {

    const [name, setName] = useState()
    const [category, setCategory] = useState()
    const [description, setDescription] = useState()
    const [price, setPrice] = useState()


    if(!show){
        return null
    }
  return (
      <div className='modal'onClick={onClose}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>

              <div className="header-modal" >
                  <h1>Add a product</h1>
                  <button onClick={onClose}>X</button>
              </div>
              <div className="body-modal">
                  <input type="text" id='name' name='name' placeholder='Name...' onChange={(e) => setName(e.target.value)} />
                  <input type="text" id='description' name='description' placeholder='Description...' onChange={(e) => setDescription(e.target.value)}/>
                  <input type="text" id='price' name='price' placeholder='Price...'onChange={(e) => setPrice(e.target.value)} />
                  <div className="categories-modal">
                      {categories.map((cat, index) => {
                          return <div className="single-category" key={index} >
                              <input type="radio" name="category" id={cat.name} value={cat._id} defaultChecked={cat.name === "Burger"} onChange={(e) => setCategory(e.target.value)} />
                              <label htmlFor={cat.name} ><p >{cat.name}</p> <img src={process.env.PUBLIC_URL + `/images/${cat.name}.png`} alt="" /></label>

                          </div>
                      })}
                  </div>
              </div>
              <div className="footer-modal">
                  <button onClick={onClose}>Back</button>
                  <button>Confirm</button>
              </div>
          </div>

      </div>
  )
}
