import React, { useState } from 'react'
import Header from '../components/header/Header'
import Menu from '../components/menu/Menu'
import Navbar from '../components/navbar/Navbar'
import Service from '../components/Service/Service'
import UpPage from '../components/upPage/UpPage'
import axios from "axios"
import "./Home.css"
import Cart from '../components/cart/Cart'
import toast, { Toaster } from 'react-hot-toast';
export default function Home() {
   const [cart, setCart] = useState([])
   const [totalPrice, setTotalPrice] = useState(0)
  

   const removeFromCart = (id) => {
   
    const index = cart.findIndex(obj => obj._id === id)

    let newCart = [...cart]
   const minusPrice = newCart[index].price
    newCart.splice(index, 1)
    setCart(newCart)
    setTotalPrice(totalPrice - minusPrice)

   }

   const addCart = async(id) => {

     await axios({
      method:"get",
      url:`${process.env.REACT_APP_API_URL}food/`+ id ,

  })
  .then((res) => {
    
    
    
    let newCart = [...cart]
    newCart.push(res.data[0])
    setCart(newCart)
    setTotalPrice(totalPrice + res.data[0].price)
    toast('Hello World', {
      duration: 4000,
      position: 'top-center',
      // Styling
      style: {},
      className: '',
      // Custom Icon
      icon: 'ðŸ‘',
      // Change colors of success/error/loading icon
      iconTheme: {
        primary: '#000',
        secondary: '#fff',
      },
      // Aria
      ariaProps: {
        role: 'status',
        'aria-live': 'polite',
      },
    })
  })

  .catch((err) => {
      console.log(err)
  })

   }

  
  return (
    <div className='container'>
      <Navbar />
      <Header />
      <Service />
      <Menu cart={cart} addCart={addCart}/>
      <UpPage />
      <Cart cart={cart} removeFromCart={removeFromCart} totalPrice={totalPrice}/>

    </div>
  )
}
