import axios from 'axios'
import React, { useEffect, useState } from 'react'
import "./myorder.css"
export default function MyOrders() {
    const [myOrders, setMyOrders] = useState([])
    const [loading, setLoading] = useState(false)
    const statusToString = (nb) => {
        if(nb===0){
            return "En attente de confirmation"
        }
        else if(nb===1){
            return "En cuisine"
        }else if (nb===2){
            return "En Route"
        } else {
            return "Livré"
        }
    }

    useEffect(()=> {
        const id = localStorage.getItem("userId")
        const getOrder = async()=> {

            await axios({
                method:"get",
                url:`${process.env.REACT_APP_API_URL}order/`+ id ,
                withCredentials: true, 
                headers: {
                    'Access-Control-Allow-Credentials': true,
            
            
                  }
                
            })
            .then((res) => {
                
                
                console.log(res.data.data)
                setMyOrders(res.data.data)
                setTimeout(() => setLoading(true),300)
                
            })
            
            .catch((err) => {
                console.log(err)
            })
        }
      getOrder()
    },[])

    if(loading === false) {
        return <h1>LOADING</h1>
    }

  return (
      <div className='container'>
    <div className='orders-container'>
        <div className="current-orders orders">
            <h1>Current Orders</h1>
        {myOrders.map((order)=> {
            if(order.status !==3){
                return <div className='single-order'>
                    <p>Status : {statusToString(order.status)}</p>
                    <p>Address : {order.address}</p>
                    <ul>
                        {order.orderItems.map((item)=> {
                           return <li>Food: {item.name}</li>
                        })}
                    </ul>

                </div>
            }
        })}
        </div>
        <div className="old-orders orders">
        <h1>Old Orders</h1>
        {myOrders.map((order)=> {
            if(order.status ===3){
                return <div className='single-order'>
                    <p>{statusToString(order.status)}</p>
                    <p>{order.address}</p>
                    <ul>
                        
                        {order.orderItems.map((item)=> {
                           return <li>{item.name}</li>
                        })}
                    </ul>

                </div>
            }
        })}
        </div>
    </div>
    </div>
  )
}
