import React, { useEffect, useRef, useState } from 'react'
import "./cart.css"
export default function Cart({cart, removeFromCart, totalPrice}) {

    const [toggle, setToggle] = useState()
    

    const cartEnd = useRef()


    const toggleCart = (e) => {
        e.preventDefault()
        const cart = document.querySelector(".cart")
        if(cart.style.height !== "100px"){
            cart.style.height = "100px"
            setToggle("˄")
            
        }else {
            cart.style.height = "auto"
            setToggle("˅")
        }

    }

  const scrollToBottom = () => {
    cartEnd.current?.scrollIntoView({ behavior: "smooth" })
  }

  useEffect(() => {
    scrollToBottom()
  }, [cart]);

  return (
      
    <div className="cart" >
        <button onClick={(e) => toggleCart(e)} className="toggle-cart"> {toggle}</button>
        <h1>Cart</h1>
        {cart.map((item, i) => {
            return <div className='cart-item' ref={cartEnd} key={i}>
                <img src={process.env.PUBLIC_URL + `/images/${item.name}.png`} alt="" width="50" height="50" />
                <span>{item.name}</span>
                <span>${item.price}</span>
                <button className='remove' onClick={() => removeFromCart(item._id)}>X</button>
                
            </div>
        })}
        <span className='total-price'>Total Price : ${totalPrice}</span>
        {totalPrice === 0 ? (<span>Add Something to cart !</span>) : <button className='order'>Order now </button>}
    </div>
  )
}
