import axios from 'axios'
import React, { useEffect, useState } from 'react'
import SideBar from '../../../components/sidebar/SideBar'
import "./products.css"
export default function Products() {
    const [allFood, setAllFood] = useState([])
    
    useEffect(() => {
        axios({
            method:"get",
            url:`${process.env.REACT_APP_API_URL}food/allfood`,
    
        })
        .then((res) => {
          console.log(res.data.data)
          setAllFood(res.data.data)
        })
        .catch((err) => {
            console.log(err)
        })
       }, [])
  return (
    <div>
        <SideBar />
        <div className="all-food">
        <table>
  <tr>
    <th>Name</th>
    <th>Description</th>
    <th>Price</th>
    <th>Category</th>
    <th>Image</th>
    <th>Action</th>
  </tr>
{allFood.map((food) => {
    return  <tr>
    <td>{food.name}</td>
    <td>{food.description}</td>
    <td>${food.price}</td>
    <td>{food.category}</td>
    <td>Image</td>
    <td><img src={process.env.PUBLIC_URL + "/images/pencil.png"} alt="" /><img src={process.env.PUBLIC_URL + "/images/remove.png"} alt="" /></td>
  </tr>
})}
 
 
</table>
        </div>
    </div>
  )
}
