import React, { useEffect, useState } from 'react'
import Header from '../components/header/Header'
import Menu from '../components/menu/Menu'
import Navbar from '../components/navbar/Navbar'
import Service from '../components/Service/Service'
import UpPage from '../components/upPage/UpPage'
import axios from "axios"
import "./Home.css"
import Cart from '../components/cart/Cart'
import Order from '../components/order/Order'
import { useNavigate } from 'react-router-dom'
import LoadingSpinner from '../components/spinner/Spinner'

export default function Home() {
   const [cart, setCart] = useState([])
   const [totalPrice, setTotalPrice] = useState(0)
   const [order, setOrder] = useState([])
   const [user, setUser] = useState([])
   const [loading, setLoading] = useState(false)
   const [myOrders, setMyOrders] = useState([])
   const [isWaitingOrder, setIsWaitingOrder] = useState(false)
   const customer = "patrick"
   const address = "12 rue francis de précensé"

   
const navigate = useNavigate()


  useEffect(() => {
    setUser(JSON.parse(localStorage.getItem("userInfo")))
    setTimeout(() => setLoading(true),500)
  }, [])

   const removeFromCart = (id) => {
   
    const index = cart.findIndex(obj => obj._id === id)

    let newCart = [...cart]
   const minusPrice = newCart[index].price
    newCart.splice(index, 1)
    setCart(newCart)
    setTotalPrice(totalPrice - minusPrice)

   }

   const addCart = async(id) => {

    if(!user){
      
      return navigate("/login")
    }
     await axios({
      method:"get",
      url:`${process.env.REACT_APP_API_URL}food/`+ id ,

  })
  .then((res) => {
    
    
    
    let newCart = [...cart]
    newCart.push(res.data[0])
    setCart(newCart)
    setTotalPrice(totalPrice + res.data[0].price)
    
  })

  .catch((err) => {
      console.log(err)
  })

   }

   useEffect(() => {
    const id = localStorage.getItem("userId")

         axios({
            method:"get",
            url:`${process.env.REACT_APP_API_URL}order/`+ id ,
            withCredentials: true, 
            headers: {
                'Access-Control-Allow-Credentials': true,
              }         
        })
        .then((res) => {             
            console.log(res.data.data)
            setMyOrders(res.data.data)
            setTimeout(() => setLoading(true),500)       
        })
        
        .catch((err) => {
            console.log(err)
        })
    },[])
   
    const isOrder = async()=> {
      let test = false
      myOrders.map((order)=> {
        console.log(order.status)
        if(order.status === 3){
           return test = true
         
        }else {
          test = false
        }

        
      })
      return test
    }

   const createOrder = async() => {
       
    let donne = await isOrder()

    console.log(donne)
        if(donne=== false) {

          await axios({
            method:"post",
            url:`${process.env.REACT_APP_API_URL}order/create`,
            withCredentials:true,
            data: {
              customer_id: user._id,
              customer: user.username,
              address: user.address,
              total: totalPrice,
              status: 0,
              orderItems: cart,
            },
            headers : {
              'Access-Control-Allow-Credentials' : true,
              
              
            }
            
          })
          .then((res) => {
            
            
            setOrder(res.data.data)
            navigate("/orders")
            
            
            
          })
          
          .catch((err) => {
            console.log(err)
          })
        } else{
          console.log("vous avez déja une commande en cours")
        }
        }

 if(loading === false){
   return <LoadingSpinner />
 }
  
  return (
    <div className='container'>
      <Navbar cart={cart} setCart={setCart} setTotalPrice={setTotalPrice}/>
      <Header />
      <Service />
      <Menu cart={cart} addCart={addCart}/>
      <UpPage />
      <Cart cart={cart} removeFromCart={removeFromCart} totalPrice={totalPrice} createOrder={createOrder} order={order}/>
      {order.length !==0? <Order order={order} /> : ""}

    </div>
  )
}
