import React, { useEffect, useRef, useState } from 'react'
import "./cart.css"
export default function Cart({cart, removeFromCart, totalPrice}) {

    


    // useEffect(() => {

    //     // Rajoute +1 au panier car il recompte ce qu'il y a dans le panier et l'ajoute 
    //     // au totalPrice tant que le cart change, nous devons le faire uniquement si nous ajoutons dans le panier
    //     // mais pas lorsque l'on supprime.
    //     cart.map((item) => (
    //         setTotalPrice(totalPrice + item.price)
    //     ))

    // }, [cart])
    const cartEnd = useRef()

  const scrollToBottom = () => {
    cartEnd.current?.scrollIntoView({ behavior: "smooth" })
  }

  useEffect(() => {
    scrollToBottom()
  }, [cart]);

  return (
      
    <div className="cart" >
        <h1>Cart</h1>
        {cart.map((item, i) => {
            return <div className='cart-item' ref={cartEnd} key={i}>
                <img src={process.env.PUBLIC_URL + `/images/${item.name}.png`} alt="" width="50" height="50" />
                <span>{item.name}</span>
                <span>${item.price}</span>
                <button className='remove' onClick={() => removeFromCart(item._id)}>X</button>
                
            </div>
        })}
        <span className='total-price'>Total Price : ${totalPrice}</span>
      

        {totalPrice === 0 ? (<span>Add Something to cart !</span>) : <button className='order'>Order now </button>}
       
    </div>
  )
}
