import React from 'react'
import "./login.css"
import axios from "axios"

export default function Login() {

  const [email, setEmail] = useState()
  const [password, setPassword] = useState()

  const handleLogin = (e) => {
       
    e.preventDefault()
    axios({
        method:"post",
        url:`${process.env.HOST}/user/login`,
        withCredentials:true,
        data: {
            email: email,
            password: password
        },
        headers : {
            'Access-Control-Allow-Credentials' : true,
            
        }
        
    })
    .then((res) => {
        console.log(res)
        if(res.data.banned){
            console.log("test")
            setBanned(true)
            setUser(res.data.user)
            
            
        }else {
            navigate("/all-users")
        }
       
    })

    .catch((err) => {
        console.log(err)
    })

}


  return (
    <>
      <div className="login-page">

          <form className='login-form'>
              <h1 className="title-auth">Login</h1>
              <input type="text" name='email' id='email' placeholder='Email...' onChange={(e) => setEmail(e.target.value)} value={email}/>
              <input type="text" name='password' id='password' placeholder='Password...'onChange={(e) => setPassword(e.target.value)} value={password}/>
              <button>Login</button>
          </form>
      </div>
    </>
  )
}
