import React, { useEffect, useRef, useState } from 'react'
import axios from "axios"
import "./cart.css"
export default function Cart({cart, removeFromCart, totalPrice}) {


    
    const cartEnd = useRef()
    const [cartId, setCartId] = useState([])
  
    const customer = "patrick"
    const address = "12 rue francis de précensé"
    console.log(cartId)
    const createOrder = async() => {
        cart.map((item) => {
            console.log(item._id)
            const newArr = [...cartId]
            newArr.push(item._id)
            setCartId(newArr)
        })
       
       await axios({
            method:"post",
            url:`${process.env.REACT_APP_API_URL}order/create`,
            withCredentials:true,
            data: {
                customer: customer,
                address: address,
                total: totalPrice,
                status: 1,
                orderItems: cartId,
            },
            headers : {
                'Access-Control-Allow-Credentials' : true,
               
                
            }
            
        })
        .then((res) => {
        
        
            
          console.log(res)
          
            
           
        })
    
        .catch((err) => {
            console.log(err)
        })
    }

  const scrollToBottom = () => {
    cartEnd.current?.scrollIntoView({ behavior: "smooth" })
  }

  useEffect(() => {
    scrollToBottom()
  }, [cart]);

  return (
      
    <div className="cart" >
        
        <h1>Cart</h1>
        {cart.map((item, i) => {
            return <div className='cart-item' ref={cartEnd} key={i}>
                <img src={process.env.PUBLIC_URL + `/images/${item.name}.png`} alt="" width="50" height="50" />
                <span>{item.name}</span>
                <span>${item.price}</span>
                <button className='remove' onClick={() => removeFromCart(item._id)}>X</button>
                
            </div>
        })}
        <span className='total-price'>Total Price : ${totalPrice}</span>
        {totalPrice === 0 ? (<span>Add Something to cart !</span>) : <button className='order' onClick={() => createOrder()}>Order now </button>}
    </div>
  )
}
