import React, { useEffect } from 'react'
import "./order.css"

export default function Order({order}) {

   
    const statusToString = (nb) => {
        if(nb===0){
            return "En attente de confirmation"
        }
        else if(nb===1){
            return "En cuisine"
        }else if (nb===2){
            return "En Route"
        } else {
            return "Livré"
        }
    }
    console.log(order)
  return (
      
    <div>
        <p>{statusToString(order.status)}</p>
        <p>{order.customer_id}</p>
       <p>{order.address}</p>
       <p>{order.customer}</p>
       <p>{order.orderItems.map((food)=>{
           return <div>
               <p>{food.name} <span>${food.price}</span></p>
                
           </div>
       })}</p>
       <p>Total Price : $ {order.total}</p>
    </div>
  )
}
