import axios from 'axios'
import React, { useEffect, useState } from 'react'

import "./menu.css"

export default function Menu() {

    const [category, setCategory] = useState([])

    console.log(category)
   useEffect(() => {
    axios({
        method:"get",
        url:`${process.env.REACT_APP_API_URL}category/allcategory`,

    })
    .then((res) => {
      
      setCategory(res.data.data)
      
      
        
       
    })

    .catch((err) => {
        console.log(err)
    })
   }, [])

  return (
      <>
          <div className="menu-title">
              <p className='our-menu'>Our menu</p>
              <p className='subtitle-menu'>Take a look about our menu</p>
          </div>

          <div className='menu'>
            {category.map((cat, index) => {
                return <div className="single-category" key={index}>
                        <input type="radio" name={cat.name} id={cat.name} value={cat._id} />
                        <label htmlFor={cat.name}>{cat.name}</label>
                </div>
            })}
          </div>
      </>
  )
}
