import React, { useEffect, useState } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'
import axios from "axios"
import "./navbar.css"
export default function Navbar({cart, setCart}) {

    const [user, setUser] = useState(null)
  
   
    const navigate = useNavigate()
    const getLogin = () => {
        navigate("/login")
    }

    const handleLogout = async(e) => {
      e.preventDefault()
      axios({
        method:"post",
        url:`${process.env.REACT_APP_API_URL}user/logout`,
        withCredentials:true,
        headers : {
            'Access-Control-Allow-Credentials' : true,
           
            
        }
        
    })
    .then((res) => {
      localStorage.removeItem("userId")
      localStorage.removeItem("userInfo")
      setCart("")
      navigate("/")
      setUser(null)
      
        
       
    })

    .catch((err) => {
        console.log(err)
    })


    }

    useEffect(()=> {
       let userId = localStorage.getItem("userId")
        setUser(userId)
    }, [])
  return (
    <>
    <div className="nav">
       <p className='logo'><NavLink exact to="/">FOODIES</NavLink> </p>
       
           <ul className='navList'>
               <li><a href="#service">Service</a> </li>
               <li><a href="#menu">Menu</a></li>
               <li><a href="#">Contact</a></li>
             {user === null ? "" : (<li><NavLink exact to="/orders">My orders</NavLink></li>)}  
           </ul>
      {user === null ? ( <button onClick={getLogin}>Login</button>): ( <button onClick={(e) => handleLogout(e)}>Logout</button>)}
   

      
      
    </div>
    
    
    </>
  )
}
