import axios from 'axios'
import React, { useEffect, useState } from 'react'
import Navbar from '../../components/navbar/Navbar'
import "./myorder.css"
export default function MyOrders() {
    const [myOrders, setMyOrders] = useState([])
    const [loading, setLoading] = useState(false)
    const statusToString = (nb) => {
        if(nb===0){
            return "Waiting confirmation"
        }
        else if(nb===1){
            return "Cooking"
        }else if (nb===2){
            return "On the way"
        } else {
            return "Delivered"
        }
    }

    const showHideFood = (e, id) => {
        e.preventDefault()
        document.querySelector("#" + id)
    }

    useEffect(()=> {
        const id = localStorage.getItem("userId")
        const getOrder = async()=> {

            await axios({
                method:"get",
                url:`${process.env.REACT_APP_API_URL}order/`+ id ,
                withCredentials: true, 
                headers: {
                    'Access-Control-Allow-Credentials': true,
            
            
                  }
                
            })
            .then((res) => {
                
                
                console.log(res.data.data)
                setMyOrders(res.data.data)
                setTimeout(() => setLoading(true),300)
                
            })
            
            .catch((err) => {
                console.log(err)
            })
        }
      getOrder()
    },[])

    if(loading === false) {
        return <h1>LOADING</h1>
    }

  return (
      
      <div className='container'>
          <Navbar />
    <div className='orders-container'>
        <div className="current-orders orders">
            <h1>  <span className='span-color'>Current</span> Orders</h1>
        {myOrders.map((order)=> {
            if(order.status !==3){
                return <div className='single-order'>
                    <p>Status : {statusToString(order.status)}</p>
                    <p>Address : {order.address}</p>
                    <button className='show-food'>Show/hide food</button>
                    <ul className='food-list' id={order._id}>
                    
                        {order.orderItems.map((item)=> {
                           return <li> <img src={process.env.PUBLIC_URL + `/images/${item.name}.png`} alt="" width="40" height="40" /> <span>{item.name}</span></li>
                        })}
                    </ul>
                    <p>Total : <span className='span-color'>$</span>{order.total}</p>

                </div>
            }
        })}
        </div>
        <div className="old-orders orders">
        <h1><span className='span-color'>Old</span> Orders</h1>
        {myOrders.map((order)=> {
            if(order.status ===3){
                return <div className='single-order'>
                    <p>Status : {statusToString(order.status)}</p>
                    <p>Adress : {order.address}</p>
                    <button  className='show-food' onClick={(e) => showHideFood(e, order._id)}>Show/hide food</button>
                    <ul className='food-list'>
                        
                        {order.orderItems.map((item)=> {
                           return <li> <img src={process.env.PUBLIC_URL + `/images/${item.name}.png`} alt="" width="40" height="40" /> <span>{item.name}</span></li>
                        })}
                    </ul>
                    <p>Total : <span className='span-color'>$</span>{order.total}</p>

                </div>
            }
        })}
        </div>
    </div>
    </div>
  )
}
